for (const item of $input.all()) {
  if (item.binary) {
    const binaryPropertyNames = Object.keys(item.binary);
    for (const propName of binaryPropertyNames) {
      if (item.binary[propName].mimeType === 'audio/mp3') {
        item.binary[propName].mimeType = 'audio/mpeg';
      }
    }
  }
}

return $input.all();

